if(!Element.prototype.addClass){Element.prototype.addClass=function(b){var a=this;requestAnimationFrame(function(){a.className=(a.className.trim()+" "+b.trim()).split(" ").filter(function(c){return c.trim()}).unique().join(" ")});return this}}if(!Element.prototype.removeClass){Element.prototype.removeClass=function(b){var a=this;requestAnimationFrame(function(){a.className=a.className.replace(new RegExp("(^|\\b)"+b.split(" ").join("|")+"(\\b|$)","gi")," ").trim().split(" ").filter(function(c){return c.trim()}).unique().join(" ")});return this}}if(!Element.prototype.setClass){Element.prototype.setClass=function(b){var a=this;requestAnimationFrame(function(){a.className=b.split(" ").filter(function(c){return c.trim()}).unique().join(" ")});return this}}if(!Element.prototype.addEventListeners){Element.prototype.addEventListeners=function(c,b){var a=this;c.split(" ").filter(function(d){return d.length}).map(function(d){a.addEventListener(d,b,false)});return a}};